    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
     
         int a=Integer.parseInt(jTextField1.getText());
          int b=Integer.parseInt(jTextField2.getText());
           int c=Integer.parseInt(jTextField3.getText());
            int d=Integer.parseInt(jTextField4.getText());
             int e=Integer.parseInt(jTextField5.getText());
              int f=Integer.parseInt(jTextField6.getText());
              if(a>b&&a>c&&a>d&&a>e&&a>f)
              jTextField7.setText("CANDIDATE 1 IS WINNER");
              else
                  if(b>c&&b>d&&b>e&&b>f)
                        jTextField7.setText("CANDIDATE 2 IS WINNER");
                     else
                      if(c>d&&c>e&&c>f)
                            jTextField7.setText("CANDIDATE 3 IS WINNER");
              else
                          if(d>e&&d>f)
                                jTextField7.setText("CANDIDATE 4 IS WINNER");
              else
                              if(d>f)
                                jTextField7.setText("CANDIDATE 5 IS WINNER");
              else
                                   jTextField7.setText("CANDIDATE 6 IS WINNER");
                                               
                               
    }//GEN-LAST:event_jButton1ActionPerformed

